DROP DATABASE IF EXISTS dietary_requirements_db;

CREATE DATABASE dietary_requirements_db;

\c dietary_requirements_db;

DROP TABLE IF EXISTS dishes;
DROP TABLE IF EXISTS users;
DROP TABLE IF EXISTS restaurants;

CREATE TABLE users(
   id              SERIAL PRIMARY KEY 
  ,username VARCHAR(14) NOT NULL
  ,password             VARCHAR(62) NOT NULL
  ,first_name         VARCHAR(58) NOT NULL
  ,last_name         VARCHAR(58) NOT NULL
  ,email         VARCHAR(58) NOT NULL
  ,is_admin   BOOLEAN  
);

CREATE TABLE dishes(
   id                        INTEGER  NOT NULL PRIMARY KEY 
  ,dish_name                 VARCHAR(47) NOT NULL
  ,type                      VARCHAR(7) NOT NULL
  ,description               VARCHAR(269)
  ,img                       VARCHAR(30)
  ,price                     VARCHAR(6) NOT NULL
  ,kcal                      INTEGER 
  ,tag                       VARCHAR(45)
  ,spicy                     INTEGER 
  ,contains                  VARCHAR(9)
  ,Dietrary_Requirments_GF   BOOLEAN  NOT NULL
  ,Dietrary_Requirments_VG   BOOLEAN  NOT NULL
  ,Dietrary_Requirments_V    BOOLEAN  NOT NULL
  ,restaurantId              INTEGER  NOT NULL
);

CREATE TABLE restaurants(
   id              INTEGER  NOT NULL PRIMARY KEY 
  ,restaurant_name VARCHAR(14) NOT NULL
  ,url             VARCHAR(62) NOT NULL
  ,address         VARCHAR(58) NOT NULL
);

